CREATE TABLE IF NOT EXISTS 'status' ( 'status_id' INTEGER PRIMARY KEY AUTOINCREMENT, 'status_name' TEXT, 'status_color' VARCHAR(8), 'status_default' BOOL);
CREATE TABLE IF NOT EXISTS "reservation" ( 'reservation_id' INTEGER PRIMARY KEY AUTOINCREMENT, 'reservation_begin' DATETIME, 'reservation_end' DATETIME, 'reservation_target' VARCHAR (24), 'reservation_status' INTEGER, 'reservation_address' text, 'reservation_locality' text, 'reservation_contact' text, reservation_comment varchar(255));
CREATE TABLE IF NOT EXISTS 'contacts' ( 'contacts_id' INTEGER PRIMARY KEY, 'contacts_reservation' INTEGER, 'contacts_target' TEXT, 'contacts_comment' VARCHAR(255));
